import os

def replace_in_file(filepath, repl_dict):
    with open(filepath, 'r', encoding='utf-8') as f:
        content = f.read()
    
    for zh, en in repl_dict.items():
        content = content.replace(zh, en)
        
    with open(filepath, 'w', encoding='utf-8') as f:
        f.write(content)

